import React, {useState} from "react"

let Contact_Form = ({ addP }) => {
    let [name, setName] = useState({name: ""});
    let [email, setEmail] = useState({email: ""});
    let [message, setMessage] = useState({message: ""});

    const handleChangeName = (e) => {
        setName(e.target.value);
    };
    const handleChangeEmail = (e) => {
        setEmail(e.target.value);
    };
    const handleChangeMessage = (e) => {
        setMessage(e.target.value);
    };



    const handleSubmit = (e) => {
        e.preventDefault();
        addP({name,email,message})
       console.log("wysłano")

    };

    return(
        <div className="Contact_Form">
            <h2>Skontaktuj sie z nami</h2>
            <a className="Contact_Form_A"></a>
            <div className="Form">
                <form onSubmit={handleSubmit} >
                <div className="Form-A">

                    <label > Wpisz swoje imie <br/>
                    <input type="text" id="name" onChange={handleChangeName}></input>
                    </label><br/>

                    <label >Wpisz swoj email<br/>
                    <input type="text" id="email" onChange={handleChangeEmail}></input>
                    </label><br/>
                </div>
                <div className="Form-B">
                    <label>Wpisz swoją wiadomość<br/>
                    <input className="input" onChange={handleChangeMessage}></input>
                    </label><br/>

                </div>
                <input className="input_Send" type="submit" value="wyślij" />
                </form>
            </div>
        </div>
    )
}
export default Contact_Form
